import { Injectable } from '@angular/core';
import { Router, CanActivate, CanActivateFn } from '@angular/router';
import { AuthService } from './auth.service';

import {MatDialog, MatDialogConfig} from "@angular/material/dialog";
import { PopupComponent } from '../popup/popup.component';

@Injectable()
export class AuthGuardService implements CanActivate {
  constructor(public auth: AuthService, private dialog: MatDialog) {}

  canActivate(): boolean {
    console.log('entered canActivate()');
    if (!this.auth.isAuthenticated()) {
      // To Do return true after dialog closed -> then redirect to requested site
      this.openDialog();
      return false;
    }
    return true;
  }

  openDialog() {
    const dialogConfig = new MatDialogConfig();

    window.addEventListener('message', (event) => {
      if (event.origin === window.location.origin && event.data === 'login-success') {
        popup.close();)
      }
    })

        dialogConfig.disableClose = true;
        dialogConfig.autoFocus = true;

        dialogConfig.data = {
          id: 1,
          title: 'Login dialog'
        };

        const dialogRef = this.dialog.open(PopupComponent, dialogConfig);

        dialogRef.afterClosed().subscribe(result => {
          console.log('The dialog was closed');
        });
  }

}
