import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

@Injectable({ 
  providedIn: 'root'
})
export class AuthService {  
  private loginUrl = 'http://localhost:8080/login/'

  constructor(private http: HttpClient) {} 

  username: string = '';
  password: string = '';

  // Check whether the user is logged in and return
  // true or false
  isAuthenticated(): Observable<boolean> {
    return this.http.get<boolean>('/api/is-authenticated');
  }
  // public isAuthenticated(): boolean {
  //   const user = sessionStorage.getItem('currentUser');
  //   return user === 'true';
  // }

  // Logs user in by setting 'currentUser' to 'true'
  login(username: string, password: string) {
    const loginData = { username: username, password: password };
    return this.http.post(this.loginUrl, loginData);

    // if (username === 'admin' && password === '123') {
    //   sessionStorage.setItem('currentUser', 'true');
    //   return true;
    //   
    // } else {
    //   return false;
    // }
  }

  // Remove currentUser from localStorage to log out
  logout(): void {
    sessionStorage.removeItem('currentUser');
  }

}
