import { Injectable } from '@angular/core';
import { Router, RouterStateSnapshot } from '@angular/router';

@Injectable({ 
  providedIn: 'root'
})
export class AuthService {  

  constructor(private router: Router) {} 

  username: string = '';
  password: string = '';

  private previousUrl: string = '';

  // Check whether the user is logged in and return
  // true or false
  public isAuthenticated(): boolean {
    const user = localStorage.getItem('currentUser');
    return user === 'true';
  }

  // Logs user in by settung 'currentUser' to 'true'
  login(): void {
    localStorage.setItem('currentUser', 'true');
  }

  getPreviousUrl(): string {
    return this.previousUrl;
  }

  // Remove currentUser from localStorage to log out
  // Afterwards redirect to login
  // Does not use query parameters
  logout(): void {
    const returnUrl = this.route.snapshot.queryParams['returnUrl'] || '/';
    this.previousUrl = window.location.pathname;
    localStorage.removeItem('currentUser');
    this.router.navigate(['/login']);
  }

}
