import { Injectable } from '@angular/core';
import { AuthService } from './auth.service';
import { CanActivate, ActivatedRouteSnapshot, RouterStateSnapshot, UrlTree } from '@angular/router';
import { Observable } from 'rxjs';


import {MatDialog, MatDialogConfig} from "@angular/material/dialog";
import { PopupComponent } from '../popup/popup.component';

@Injectable()
export class AuthGuardService implements CanActivate {
  constructor(public auth: AuthService, private dialog: MatDialog) {}

  canActivate(
    route: ActivatedRouteSnapshot,
    state: RouterStateSnapshot): Observable<boolean | UrlTree> | Promise<boolean | UrlTree> | boolean | UrlTree {

    // Check if user is authenticated
    const authenticated = !localStorage.getItem('currentUser')
    if (authenticated) {
      return true; // User is authenticated, allow access to the protected route
    } else {
      // User is not authenticated, open login dialog and wait for user to log in
      const dialogRef = this.dialog.open(PopupComponent, {
        width: '300px',
        disableClose: true, // Prevents user from closing dialog by clicking outside or pressing escape
        autoFocus: true
      });

      return dialogRef.componentInstance.loggedIn; // Wait for user to log in before allowing access to the protected route
    }
  }
