import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Observable } from 'rxjs';

@Injectable({ 
  providedIn: 'root'
})
export class AuthService {  
  private authUrl = 'http://localhost:8080/accounts/'

  constructor(private http: HttpClient) {} 

  username: string = '';
  password: string = '';

  // Check whether the user is logged in and return
  // true or false
  isAuthenticated(): Observable<boolean> {
    const checkUrl = `${this.authUrl}is-authenticated/`;
    return this.http.get<boolean>(checkUrl);
  }

  // Logs user in by setting 'currentUser' to 'true'
  login(username: string, password: string): Observable<any> {
    const loginUrl = `${this.authUrl}login/`;
    const loginData = { username: username, password: password };

    const headers = new HttpHeaders().set('Content-Type', 'application/json');

    return this.http.post(loginUrl, loginData, { headers: headers });
  }

  // Call view to log user out
  logout(): Observable<any> {
    console.log("entered authservice logout")
    const logoutUrl = `${this.authUrl}/logout/`;
    console.log("logout url "+ logoutUrl)
    return this.http.post(logoutUrl, null);
  }
}
