import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Injectable({ 
  providedIn: 'root'
})
export class AuthService {  
  private loginUrl = 'http://localhost:8080/login/'

  constructor(private http: HttpClient) {} 

  username: string = '';
  password: string = '';

  // Check whether the user is logged in and return
  // true or false
  public async isAuthenticated(): Promise<boolean> {
    try {
      const isAuthenticated = await this.http.get<boolean>(this.loginUrl).toPromise();
      return isAuthenticated || false;
    } catch (error) {
      console.error('Error occurred while checking authentication:', error);
      return false;
    }
  }

  // Logs user in by setting 'currentUser' to 'true'
  login(username: string, password: string) {
    const loginData = { username: username, password: password };
    return this.http.post(this.loginUrl, loginData);
  }

  // Remove currentUser from localStorage to log out
  logout(): void {
    sessionStorage.removeItem('currentUser');
  }

}
