import { Component, ViewChild } from '@angular/core';
import { Router, ActivatedRoute } from '@angular/router';


import { AuthService } from '../auth/auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css'],

})
export class LoginComponent { 

  username: string = '';
  password: string = '';

  @ViewChild('loginPopup') loginPopup: any;

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private authService: AuthService,
  ) {}

  isLoggedIn() {
    console.log("login component entered isLoggedIn")
    return this.authService.isAuthenticated();
  }

  // Gets 'username' and 'password' of inputs
  // compares it against credentials
  // logs in user and closes window and redirects
  // or raises alert
  onSubmit() {
    const username = (<HTMLInputElement>document.getElementsByName('username')[0]).value;
    const password = (<HTMLInputElement>document.getElementsByName('password')[0]).value;
    this.authService.login(username, password).subscribe(
      (response) => {
        // Successful login
        console.log('Login successful:', response);
        // Redirect the user to the authenticated section of your website
        window.location.href = '/authenticated-section'; // Replace with your actual URL
      },
      (error) => {
        // Error handling
        console.error('Login error:', error);
        // Handle the error and display an appropriate message to the user
      }
    );
  }
}
