import { Component, AfterViewInit, ViewChild } from '@angular/core';
import { Router, ActivatedRoute } from '@angular/router';


import { AuthService } from '../auth/auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css'],

})
export class LoginComponent implements AfterViewInit{ 

  username: string = '';
  password: string = '';

  @ViewChild('loginPopup') loginPopup: any;

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private authService: AuthService,
  ) {}

  ngAfterViewInit() {
    this.loginPopup.showModal
  }

  // Gets 'username' and 'password' input
  // Compares it against credentials
  // Logs user in and closes window and redirects
  onSubmit() {
    const username = (<HTMLInputElement>document.getElementsByName('username')[0]).value;
    const password = (<HTMLInputElement>document.getElementsByName('password')[0]).value;

    if (username === 'admin' && password === '123') {
      this.authService.login();
      this.loginPopup.nativeElement.close();
      const returnUrl = this.route.snapshot.queryParams['returnUrl'] || '/';
      this.router.navigateByUrl(returnUrl);
    } else {
      alert('Username or password is incorrect')
    }


  } 

}
