import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Injectable({ 
  providedIn: 'root'
})
export class AuthService {  

  constructor(private http: HttpClient) {} 

  username: string = '';
  password: string = '';

  // Check whether the user is logged in and return
  // true or false
  public isAuthenticated(): boolean {
    const user = sessionStorage.getItem('currentUser');
    return user === 'true';
  }

  // Logs user in by setting 'currentUser' to 'true'
  login(username: string, password: string): Promise<boolean> {
    const loginStatus = this.login(username, password)
  }

  // Remove currentUser from localStorage to log out
  logout(): Promise<any> {
    return this.http.post('/logout/', {}).toPromise()
      .then(response => {
        sessionStorage.removeItem('currentUser');
      })
      .catch(error => {
        console.error('Logout failed:', error);
      });
  }

}
