import { Injectable } from '@angular/core';
import { Router, ActivatedRouteSnapshot, RouterStateSnapshot } from '@angular/router';

import { AuthService } from './auth/auth.service';


@Injectable({ providedIn: 'root' })
export class AuthGuard {
    constructor(
      private router: Router,
        private authService: AuthService,
    ) {}

    // If user is authenticated they will be allowed to pass
    // else it will save the requested url, open a login popup
    // and after successful login redirect them to requested url
    canActivate(state: RouterStateSnapshot) {
      const authenticated = this.authService.isAuthenticated();


      if (authenticated === true) {
        return true;
      } else {
        this.router.navigate(['/login'], { queryParams: { returnUrl: state.url }});
        console.log("Navigated to login and about to return false")
        return false;
      }
    }
}
