import { Component } from '@angular/core';
import { AuthService } from './auth/auth.service';
import { Observable, map } from 'rxjs';
import { Router } from '@angular/router';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'Tour of Heroes';
  isLoggedIn$!: Observable<boolean>;

  constructor(
    private authService: AuthService,
    private router: Router,
  ) {this.isLoggedIn$ = this.authService.isAuthenticated();}

  isLoggedIn(): Observable<boolean> {
    return this.authService.isAuthenticated().pipe(
      map((response: boolean) => response === true)
    );
  }

  logout(): void {
    console.log("app component entered logout")
    const currentUrl = window.location.pathname;
    console.log(currentUrl)
    this.authService.logout();
    console.log("app component logged user out")
    this.router.navigate(['/accounts/login'], { queryParams: { returnUrl: currentUrl } });
    console.log("navigated to /accounts/login")
  }
}

<ng-container *ngIf="(isLoggedIn() | async); else loginHeader">
  <header>
    <h1>{{title}}</h1>
    <nav>
      <a routerLink="/dashboard" routerLinkActive="active">Dashboard</a>
      <a routerLink="/heroes" routerLinkActive="active">Heroes</a>
      <a class="logout-btn" (click)="logout()">Logout</a>
    </nav>
  </header>
  
  <main>
    <router-outlet></router-outlet>
    <app-messages></app-messages>
  </main>
</ng-container>

<ng-template #loginHeader>
  <header>
    <app-login></app-login>
  </header>
</ng-template>
