        return true;
      } else {
          const dialogRef = this.dialog.open(LoginComponent, {
            disableClose: true
          });
          return dialogRef.afterClosed().toPromise().then(result => {
            if (result) {                 
              this.router.navigateByUrl(returnUrl);
              return true;
            } else {
              return false;
            }
          });
        }
    }
    */
}
