import { Injectable } from '@angular/core';
import { Router, ActivatedRouteSnapshot, RouterStateSnapshot } from '@angular/router';

import { AuthService } from './auth/auth.service';
import { LoginComponent } from './login/login.component';
import { MatDialog } from '@angular/material/dialog';

@Injectable({ providedIn: 'root' })
export class AuthGuard {
    constructor(
        private router: Router,
        private authService: AuthService,
        private dialog: MatDialog,
    ) {}

    // If user is authenticated they will be allowed to pass
    // else it will save the requested url. open a login popup
    // and after successful login redirect them to requested url
    canActivate(route: ActivatedRouteSnapshot, state: RouterStateSnapshot) {
      console.log("Checks if canActivate")
      console.log(location.href)
      console.log(location.pathname)
      console.log(state.url) // /dashboard
      const authenticated = this.authService.isAuthenticated();
      if (authenticated) {
        console.log("Is authenticated")
        return true;
      } else {
        console.log("Is not authenticated")

          const dialogRef = this.dialog.open(LoginComponent, {
            width: '400px',
            disableClose: true
          });
          console.log("Created popup")
          return dialogRef.afterClosed().toPromise().then(result => {

            if (result) {
              const returnUrl = state.url;                  
              this.router.navigateByUrl(returnUrl);
              return true;
            } else {
              return false;
            }
          });
      }
    }
}
