import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Injectable({
  providedIn: 'root',
})
export class CsrfTokenService {
  constructor(private http: HttpClient) {}

  getCsrfToken() {
    return this.http.get('/api/csrf-token/', { responseType: 'text' });
  }
}
