import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

@Injectable({ 
  providedIn: 'root'
})
export class AuthService {  
  private authUrl = 'http://localhost:8080'

  constructor(private http: HttpClient) {} 

  username: string = '';
  password: string = '';

  // Check whether the user is logged in and return
  // true or false
  isAuthenticated(): Observable<boolean> {
    const authUrl = this.authUrl + '/is-authenticated/'
    return this.http.get<boolean>(authUrl);
  }

  // Logs user in by setting 'currentUser' to 'true'
  login(username: string, password: string) {
    const loginUrl = this.authUrl + '/login/';
    const loginData = { username: username, password: password };
    return this.http.post(loginUrl, loginData);
  }

  // Remove currentUser from localStorage to log out
  logout(): Observable<any> {
    const logoutUrl = this.authUrl + '/logout/';
    return this.http.post(logoutUrl, null);
}

}
